import {ADD_PRODUCT, ADD_PRODUCT_SUCCESS} from './productTypes'
import axios from 'axios'


export const productList = () => {

    return (dispatch) => {
        dispatch({
            type: ADD_PRODUCT
        })
        axios.get("https://jsonplaceholder.typicode.com/posts")
        .then(response => {
            dispatch({
                type: ADD_PRODUCT_SUCCESS,
                payload: response.data
            })
        }).catch(error => {
            console.log(error);
        })
    }

}
